#include <iostream>#include <mpi.h>int main(int argc, char **argv){    MPI::Init(argc, argv);    int procs = MPI::COMM_WORLD.Get_size();    int const ndims = 1;    int dims[ndims] = {0};    MPI::Compute_dims(procs, ndims, dims);    bool periods[ndims] = {true};    MPI::Cartcomm COMM_CART = MPI::COMM_WORLD.Create_cart(        ndims, dims, periods, true);    int rank = COMM_CART.Get_rank();    int send_value = rank;    int recv_value = -1;    int src_rank, dest_rank;    COMM_CART.Shift(0, 1, src_rank, dest_rank);    const int TAG = 0;    COMM_CART.Sendrecv(&send_value, 1, MPI::INT, dest_rank, TAG,                       &recv_value, 1, MPI::INT, src_rank , TAG);    std::cout << "rank = " << rank              << " : recv_value = " << recv_value << std::endl;    MPI::Finalize();}